package com.hugh.basis.activities;import android.app.Activity;import android.os.Bundle;import android.support.v4.app.Fragment;import android.support.v7.app.AppCompatActivity;import android.view.View;import android.view.Window;import com.hugh.basis.R;import com.hugh.basis.fragments.ContentFragment;import com.hugh.basis.fragments.NameContentFragment;import com.hugh.basis.fragments.NameFragment;/** * Created by chenyw on 2019/5/24. */public class FragmentActivity extends AppCompatActivity implements NameFragment.showPro {    private NameContentFragment cf;    private NameFragment nf;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_fragment);        cf = (NameContentFragment)getSupportFragmentManager().findFragmentById(                R.id.content_fg);        nf = (NameFragment) getSupportFragmentManager().findFragmentById(R.id.name_fg);    }    @Override    public void showProByName(String name) {        cf.showPro(name);    }    public void onClick(View v) {        switch (v.getId()) {            case R.id.clear_data:                nf.clearData();                break;            default:                break;        }    }}