package com.hugh.basis.fragments;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageButton;import android.widget.Toast;import com.hugh.basis.R;import androidx.annotation.NonNull;import androidx.annotation.Nullable;import androidx.fragment.app.Fragment;/** * Created by chenyw on 2019/5/24. */public class TitleFragment extends Fragment {    private ImageButton mLeftMenu;    @Nullable    @Override    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {        View view  = inflater.inflate(R.layout.fragment_title,container,false);        mLeftMenu  = view.findViewById(R.id.id_title_left_btn);        mLeftMenu.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Toast.makeText(getActivity(),                        "i am an ImageButton in TitleFragment ! ",                        Toast.LENGTH_SHORT).show();            }        });        return view;    }}